function fix_umlauts(string) {
  return string.replace(/[Ã¤Ã¶Ã¼ÃŸ]/ig, function (str) {
    switch (str.toLowerCase()) {
      case 'Ã¤': return 'ae';
      case 'Ã¶': return 'oe';
      case 'Ã¼': return 'ue';
      case 'ÃŸ': return 'ss';
    }
    return str;
  });
}


/**
 * Charlatan.Internet.email([name]) -> String
 * - name (string) : user name, optional
