exports.IPv6 = function () {
  var group, result = [], i, j;

  for (i = 0; i < 8; i++) {
    group = '';
    for (j = 0; j < 4; j++) {
      group += Helpers.rand(16).toString(16);
    }
    result.push(group);
  }

  return result.join(':');
};


