  if (!glue) {
    glue = Helpers.sample('-_.'.split(''));
  }

  if (!words) {
    words = Charlatan.Lorem.words(2);
  }

  if (Array.isArray(words)) {
    words = words.join(' ');
  }

  return words
           .replace(/ /g, glue)
           .toLowerCase();
};


